mapper.select:
SELECT 
FROM role
WHERE id = #{id} AND tenant_id = #{tenantId}

SELECT 
FROM role
WHERE role_code = #{roleCode} AND tenant_id = #{tenantId}

SELECT 
FROM role
WHERE tenant_id = #{tenantId}

    AND status = #{status}

ORDER BY sort_order, id

mapper.insert:
INSERT INTO role (
    tenant_id, role_name, role_code, permissions, description, is_built_in, sort_order,
    status, created_by, created_at
) VALUES (
    #{tenantId}, #{roleName}, #{roleCode}, #{permissions}, #{description}, #{isBuiltIn},
    #{sortOrder}, #{status}, #{createdBy}, #{createdAt}
)

mapper.update:
UPDATE role
role_name = #{roleName}, permissions = #{permissions}, description = #{description}, sort_order = #{sortOrder}, status = #{status}, updated_by = #{updatedBy}, updated_at = #{updatedAt}
WHERE id = #{id}
