mapper.select:
SELECT 
FROM user
WHERE id = #{id} AND tenant_id = #{tenantId}

SELECT 
FROM user
WHERE username = #{username} AND tenant_id = #{tenantId}

mapper.insert:
INSERT INTO user (
    tenant_id, username, password, real_name, email, phone, role_id, role_code, role_name,
    status, avatar, remark, created_by, created_at
) VALUES (
    #{tenantId}, #{username}, #{password}, #{realName}, #{email}, #{phone},
    #{roleId}, #{roleCode}, #{roleName}, #{status}, #{avatar}, #{remark},
    #{createdBy}, #{createdAt}
)

mapper.update:
UPDATE user
real_name = #{realName}, email = #{email}, phone = #{phone}, role_id = #{roleId}, role_code = #{roleCode}, role_name = #{roleName}, status = #{status}, avatar = #{avatar}, remark = #{remark}, updated_by = #{updatedBy}, updated_at = #{updatedAt}
WHERE id = #{id}

UPDATE user
real_name = #{user.realName}, email = #{user.email}, phone = #{user.phone}, role_id = #{user.roleId}, role_code = #{user.roleCode}, role_name = #{user.roleName}, status = #{user.status}, avatar = #{user.avatar}, remark = #{user.remark}, updated_at = #{user.updatedAt}
WHERE id = #{user.id} AND tenant_id = #{tenantId}

mapper.select:
SELECT 
FROM user
WHERE tenant_id = #{tenantId}

    AND username LIKE CONCAT('%', #{username}, '%')


    AND status = #{status}


    AND role_id = #{roleId}

ORDER BY id DESC

SELECT COUNT(*)
FROM user
WHERE tenant_id = #{tenantId}

mapper.update:
UPDATE user
SET last_login_time = NOW(),
    last_login_ip = #{ip}
WHERE id = #{userId} AND tenant_id = #{tenantId}
